const Player = ({ estadoPlayer, dispatch }) => {
  const { atual, tocando, prog, vol } = estadoPlayer;
  const playPauseIcon = tocando ? 'icon-pause' : 'icon-play';
  const togglePlay = () => dispatch({ type: 'TOGGLE_PLAY' });
  
  let volumeEmoji;
  if (vol === 0) {
      volumeEmoji = 'ðŸ”‡';
  } else if (vol <= 33) {
      volumeEmoji = 'ðŸ”ˆ';
  } else if (vol <= 66) {
      volumeEmoji = 'ðŸ”‰';
  } else {
      volumeEmoji = 'ðŸ”Š';
  }

  return (
    <div className="player">
      <div style={{ display: 'flex', alignItems: 'center' }}>
        {atual.urlCapa && (
             <img 
                 src={atual.urlCapa} 
                 alt={`Capa de ${atual.tit}`} 
                 style={{ width: '50px', height: '50px', marginRight: '15px', borderRadius: '4px' }} 
             />
         )}
        <div style={{ marginRight: '15px' }}>
          <p style={{ margin: 0, fontSize: '0.9em', fontWeight: 'bold' }}>{atual.tit || 'Nenhuma mÃºsica'}</p>
          <p style={{ margin: 0, fontSize: '0.7em', color: 'var(--text-s)' }}>{atual.art}</p>
        </div>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', flexGrow: 1, maxWidth: '600px' }}>
        <div style={{ display: 'flex', gap: '15px' }}>
            <button className="btn icon icon-prev" title="Anterior"></button>
            <button className={`btn icon ${playPauseIcon}`} onClick={togglePlay} title={tocando ? 'Pausar' : 'Tocar'}></button>
            <button className="btn icon icon-next" title="PrÃ³xima"></button>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', width: '100%', marginTop: '5px' }}>
          <span style={{ fontSize: '0.7em', color: 'var(--text-s)' }}>0:{prog < 10 ? '0' : ''}{prog}</span>
          <div className="prog-bar-wrap" style={{ cursor: 'pointer' }}>
            <div className="prog-bar" style={{ width: `${(prog / 60) * 100}%` }}></div>
          </div>
          <span style={{ fontSize: '0.7em', color: 'var(--text-s)' }}>{atual.dur || '0:00'}</span>
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', width: '120px' }}>
          <span style={{ fontSize: '20px' }} title={`Volume: ${vol}%`}>
              {volumeEmoji}
          </span>
          <input
              type="range"
              min="0"
              max="100"
              value={vol}
              onChange={(e) => dispatch({ type: 'SET_VOLUME', payload: Number(e.target.value) })}
              style={{ width: '100%', height: '4px', cursor: 'pointer' }}
          />
      </div>
    </div>
  );
};

const MusicaCard = ({ musica, isFav, playlists, dispatch }) => {
    const favIcon = isFav ? 'icon-fav-filled' : 'icon-fav';
    const [mostrarMenu, setMostrarMenu] = useState(false);

    const handleAddToPlaylist = (playlistName) => {
        dispatch({ type: 'ADD_MUSICA_TO_PLAYLIST', payload: { playlist: playlistName, musicaId: musica.id } });
        setMostrarMenu(false);
    };

    return (
        <div className="track-card" style={{ 
            backgroundColor: 'var(--bg-s)', 
            padding: '15px', 
            borderRadius: '8px', 
            width: '100%',
            maxWidth: '180px',
            boxShadow: '0 4px 10px rgba(0,0,0,0.5)',
            position: 'relative',
        }}>
            <div 
                onClick={() => dispatch({ type: 'SET_MUSICA', payload: musica })}
                style={{ 
                    backgroundColor: musica.urlCapa ? 'transparent' : '#282828', 
                    height: '150px', 
                    borderRadius: '4px', 
                    marginBottom: '10px', 
                    cursor: 'pointer', 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center', 
                    fontSize: '2em',
                    overflow: 'hidden'
                }}
            >
                {musica.urlCapa ? (
                    <img 
                        src={musica.urlCapa} 
                        alt={`Capa de ${musica.tit}`} 
                        style={{ width: '100%', height: '100%', objectFit: 'cover' }} 
                    />
                ) : (
                    'ðŸŽµ'
                )}
            </div>
            
            <p style={{ margin: 0, fontWeight: 'bold' }}>{musica.tit}</p>
            <p style={{ margin: '0 0 10px 0', fontSize: '0.8em', color: 'var(--text-s)' }}>{musica.art}</p>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <button 
                  className={`btn fav-btn icon ${favIcon}`} 
                  onClick={() => dispatch({ type: 'TOGGLE_FAV', payload: musica.id })}
                  title={isFav ? 'Desfavoritar' : 'Favoritar'}
                ></button>

                <button 
                    className="btn icon icon-add" 
                    onClick={() => setMostrarMenu(!mostrarMenu)}
                    style={{ fontSize: '1.2em' }}
                ></button>

                {mostrarMenu && (
                    <div style={{
                        position: 'absolute',
                        bottom: '70px',
                        right: '15px',
                        backgroundColor: '#303030',
                        borderRadius: '4px',
                        boxShadow: '0 4px 10px rgba(0,0,0,0.8)',
                        zIndex: 10
                    }}>
                        <p style={{ padding: '5px 10px', margin: 0, color: 'var(--purple)', fontSize: '0.9em' }}>Adicionar a:</p>
                        {playlists.map((p, idx) => (
                            <div 
                                key={idx} 
                                style={{ padding: '5px 10px', cursor: 'pointer', borderTop: '1px solid #404040' }}
                                onClick={() => handleAddToPlaylist(p.nome)}
                            >
                                {p.nome}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const ListaMusicas = ({ musicas, favs, dispatch, termoBusca, secaoAtual, playlists }) => {
  
    const musicasBase = useMemo(() => {
        if (secaoAtual === 'Home') {
            return musicas;
        } else if (secaoAtual === 'Favs') {
            return musicas.filter(m => favs.includes(m.id));
        } else if (secaoAtual.startsWith('Playlist:')) {
            const nomePlaylist = secaoAtual.replace('Playlist:', '');
            const playlistSelecionada = playlists.find(p => p.nome === nomePlaylist);
            if (playlistSelecionada) {
                return musicas.filter(m => playlistSelecionada.musicas.includes(m.id));
            }
            return [];
        }
        return musicas;
    }, [musicas, favs, secaoAtual, playlists]);

    const musicasFiltradas = useMemo(() => {
      if (!termoBusca) return musicasBase;
      const termo = termoBusca.toLowerCase();
      return musicasBase.filter(m => 
        m.tit.toLowerCase().includes(termo) || 
        m.art.toLowerCase().includes(termo)
      );
    }, [musicasBase, termoBusca]);

  const nomeSecao = secaoAtual.startsWith('Playlist:') 
    ? secaoAtual.replace('Playlist:', 'Playlist: ') 
    : secaoAtual;

  return (
    <div className="lista-musicas">
      <h2>{nomeSecao} ({musicasFiltradas.length})</h2>
      
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))', gap: '20px' }}>
        {musicasFiltradas.map((m) => (
          <MusicaCard 
            key={m.id} 
            musica={m} 
            isFav={favs.includes(m.id)} 
            playlists={playlists}
            dispatch={dispatch} 
          />
        ))}
      </div>
    </div>
  );
};

const Playlists = ({ playlists, dispatch, secaoAtual }) => {
  const [novoNome, setNovoNome] = useState('');

  const criarPlaylist = (e) => {
    e.preventDefault();
    if (novoNome.trim()) {
      dispatch({ type: 'ADD_PLAYLIST', payload: novoNome.trim() });
      setNovoNome('');
    }
  };

  const excluirPlaylist = (nome) => {
    if (window.confirm(`Tem certeza que deseja excluir a playlist "${nome}"?`)) {
        dispatch({ type: 'DELETE_PLAYLIST', payload: nome });
    }
  };

  return (
    <div className="playlists-comp">
      <h3>Criar Playlist</h3>
      <form onSubmit={criarPlaylist} className="playlist-form">
        <input
          type="text"
          value={novoNome}
          onChange={(e) => setNovoNome(e.target.value)}
          placeholder="Nome da nova playlist"
          className="playlist-input"
        />
        <button type="submit" className="playlist-btn icon icon-add">Criar</button>
      </form>
      
      <h3>Suas Playlists</h3>
      <ul>
        {playlists.map((p, idx) => {
            const isSelected = secaoAtual === `Playlist:${p.nome}`;
            return (
                <li 
                    key={idx} 
                    style={{ 
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        color: isSelected ? 'var(--text-p)' : 'var(--text-s)', 
                        marginBottom: '5px', 
                        cursor: 'pointer',
                        fontWeight: isSelected ? 'bold' : 'normal'
                    }}
                >
                    <span onClick={() => dispatch({ type: 'SET_SECTION', payload: `Playlist:${p.nome}` })}>
                        {p.nome} ({p.musicas.length})
                    </span>
                    <button 
                        className="btn"
                        onClick={() => excluirPlaylist(p.nome)}
                        style={{ color: 'var(--text-s)', fontSize: '0.8em', padding: '2px' }}
                        title={`Excluir ${p.nome}`}
                    >
                        ðŸ—‘ï¸
                    </button>
                </li>
            );
        })}
      </ul>
    </div>
  );
};

